mapper.insert:
INSERT INTO t_board_cmt
(boardPk, seq, writerPk, ctnt)
SELECT #{boardPk}, IFNULL(MAX(seq), 0)+1, #{writerPk}, #{ctnt}
FROM t_board_cmt
WHERE boardPk = #{boardPk}

mapper.update:
UPDATE t_board_cmt
SET ctnt = #{ctnt}
WHERE	boardPk = #{boardPk}
AND seq = #{seq}
AND writerPk = #{writerPk}

mapper.delete:
DELETE FROM t_board_cmt
WHERE boardPk = #{boardPk}
AND seq = #{seq}
AND writerPk = #{writerPk}
