mapper.select:
SELECT userPk, userId, userPw, salt, nm, gender, profileImg
, DATE_FORMAT(regDt, '%Y년 %m월 %d일 (%H:%i)') as regDt 
FROM t_user


		userId = #{userId}


		userPk = #{userPk}

mapper.update:
UPDATE t_user


		userPw = #{userPw},


		salt = #{salt},


		profileImg = #{profileImg}


WHERE userPk = #{userPk}
